import {useState} from "react";
import MovieList from "./MovieList.jsx";

const initMovieData = [
    {
        id: 1,
        title: '인천상륙작전',
        posterUrl: 'data:image/jpeg;base64,',
        reviews: []
    },
    {
        id: 2,
        title: '군도',
        posterUrl: 'data:image/jpeg;base64,',
        reviews: []
    },
    {
        id: 3,
        title: 'The Dark Knight',
        posterUrl: 'https://image.tmdb.org/t/p/w500/qJ2tW6WMUDux911r6m7haRef0WH.jpg',
        reviews: []
    },
    {
        id: 4,
        title: 'The Dark Knight',
        posterUrl: 'https://image.tmdb.org/t/p/w500/qJ2tW6WMUDux911r6m7haRef0WH.jpg',
        reviews: []
    },
];

function MovieReviewApp() {

    const [movies, setMovies] = useState(initMovieData);

    const addReview = (movieId, reviewContent) => {
        setMovies(movies.map(movieData =>
            movieData.id === movieId
                ? { ...movieData, reviews: [...movieData.reviews, reviewContent] }
                : movieData
        ));
    };

    return (
        <div className={`container mt-5`}>
            <h1 className={`text-center mb-4`}>영화 리뷰 어플리케이션</h1>
            <MovieList movies={movies} addReview={addReview} />
        </div>
    );
}

export default MovieReviewApp
